mapper.select:
SELECT a.user_id, a.user_password, a.permission_id, a.user_name, a.birth_date, a.sex, CASE a.sex WHEN '1' THEN '男' WHEN '2' THEN '女' END AS gender, a.email, a.phone, a.department_id, a.class_id, a.admission_date, a.avatar, b.department_name, IFNULL(c.class_name, '') as class_name, IFNULL(c.grade, '') as grade FROM user_info a LEFT JOIN department b ON a.department_id = b.department_id LEFT JOIN class_info c ON a.class_id = c.class_id
a.permission_id = #{permissionId}
AND a.user_id = #{userId}
AND a.department_id = #{depId}
AND a.class_id = #{classId}

mapper.update:
UPDATE user_info
	SET user_password = #{password}
WHERE
	email = #{email}
